package loopjava;

public class Task4Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int b = 20;
		while (b<=100) {
			
			System.out.print(b+" ");
			b+=2;
		}
		/////////////////
		
		int c= 100;
		while(100>=1) {
			System.out.print(c+" ");
			c-=2;
		}
		
		
		
	}

}
